mapper.update:
UPDATE TB_RSTRNT_INFO
SET R_COUNT = R_COUNT+1
WHERE R_NO=#{rNo}

mapper.select:
SELECT COUNT(*)
FROM TB_PHOTOFILES
WHERE R_NO=#{rNo}

SELECT MENU_NAME, TO_CHAR(MENU_PRICE, '999,999,999,999,999') AS MENU_PRICE
FROM TB_RSTRNT_MENU
WHERE R_NO=#{rNo}

SELECT *
FROM TB_REVIEW_IMAGE_LIST
WHERE REV_NO = #{revNo}
AND R_NO = #{rNo}
ORDER BY FILE_NO

SELECT COUNT(*)
FROM TB_REVIEW_IMAGE_LIST
WHERE REV_NO = #{revNo}
AND R_NO = #{rNo}
ORDER BY FILE_NO

SELECT *
FROM TB_REVIEW
WHERE REV_NO = #{ revNo }
AND R_NO = #{ rNo }
AND REV_STATUS = 'Y'

SELECT ATCHMNFL_COURS
FROM TB_REVIEW_IMAGE_LIST
WHERE REV_NO = #{ revNo }
AND R_NO = #{ rNo }
AND FILE_NO = #{ fileNo }

update.foreach:
			UPDATE TB_REVIEW_IMAGE_LIST SET FILE_NO = #{ index }
			WHERE FILE_NO = (SELECT FILE_NO
							 FROM (SELECT ROWNUM AS NUM, A.*
              				 	   FROM TB_REVIEW_IMAGE_LIST A
              					   WHERE REV_NO = #{ revNo }
              					   AND R_NO = #{ rNo }

              					   ) 
        					 WHERE NUM = #{ index })

mapper.select:
SELECT COUNT(*)
FROM TB_FAVOR
WHERE R_NO = #{ rNo }

SELECT *
FROM TB_FAVOR
WHERE R_NO = #{ rNo } 
AND M_NO = #{ mNo }
