mapper.select:
SELECT COUNT(*)
FROM TB_MANAGER
WHERE MNG_ID=#{mngId}

mapper.update:
UPDATE TB_MEMBER
SET M_STATUS = 'N'
WHERE M_NO = = #{mngNo}

mapper.select:

SELECT * FROM TB_MANAGER G, TB_MEMBER M

	G.MNG_NO = M.M_NO
	
		
			AND M.M_ID LIKE #{sv}
		
		
			AND M.M_NAME LIKE #{sv}
		
		
	

ORDER BY M.M_ID DESC
